import { useState, useEffect } from "react"

function useStats() {
    const [ stats, setStats ] = useState()

    console.log("Fetching data")

    useEffect(() => {
        async function fetchData() {
            const data = await fetch("https://alerta-corona-backend.herokuapp.com/markers")
                .then(data => data.json())

            setStats(data)
        }

        fetchData()
    }, [])

    return stats
}

export default function IndexPage() {
    const stats = useStats()
    console.log(stats)
    return <h1>Great!</h1>
}
